import React, {useContext} from 'react';
import {observer} from "mobx-react-lite";
import {Context} from "../index";
import {Pagination} from "react-bootstrap";

const Pages = observer(() => {
    const {cosmetics} = useContext(Context)
    const pageCount = Math.ceil(cosmetics.totalCount / cosmetics.limit)
    const pages = []

    for (let i = 0; i < pageCount; i++) {
        pages.push(i + 1)
    }

    return (
        <Pagination className="Cont2 mt-3">
            {pages.map(page =>
                <Pagination.Item className="Page"
                    key={page}
                    active={cosmetics.page === page}
                    onClick={() => cosmetics.setPage(page)}
                >
                    {page}

                </Pagination.Item>
            )}
        </Pagination>
    );
});

export default Pages;
