import React, {useEffect, useState} from 'react';
import {Button, Card, Col, Container, Image, Row} from "react-bootstrap";

const CosmeticsPage = () => {
    const [cosmetics, setCosmetics] = useState({info: []})

    return (
        <Container className="mt-5">
            <Row>
                <Col md={4}>
                    <Image className="Img" width={301} height={409} src={process.env.REACT_APP_API_URL + cosmetics.img}/>
                </Col>
                <Col md={4}>
                    <Row className="d-flex flex-column align-items-center">
                        <h2>{cosmetics.name}</h2>
                        <div
                            className="Star d-flex align-items-center justify-content-center"
                            style={{background: ` no-repeat center center`, width:130, height: 130, backgroundSize: 'multiply', fontSize:64}}
                        >
                            {cosmetics.rating} ★★★★★ Rating
                        </div>
                    </Row>
                </Col>
                <Col md={4}>
                    <Card
                        className="Cont d-flex flex-column align-items-center justify-content-around"
                        style={{width: 300, height: 300}}
                    >
                        <h3>From: {cosmetics.price} $</h3>
                        <Button className="But" variant={"outline"} style={{ color: '#8A496B'}}>Add to Basket</Button>
                    </Card>
                </Col>
            </Row>
            <Row className="d-flex flex-column m-3">
                <h1 className="Char">Characteristic:</h1>
                {cosmetics.info.map((info, index) =>
                    <Row key={info.id} style={{background: index % 2 === 0 ? 'lightgray' : 'transparent', padding: 10}}>
                        {info.title}: {info.description}
                    </Row>
                )}
            </Row>
        </Container>
    );
};

export default CosmeticsPage;
